// Welcome.js
import React, { Component } from 'react';
import './Welcome.css';
import { Calendar } from 'primereact/calendar';
import { AutoComplete } from 'primereact/autocomplete';
import App from '../VoiceResolver/App';

class Welcome extends Component {
  constructor(props) {
    super(props);

    let today = new Date();
    let month = today.getMonth();
    let year = today.getFullYear();
    let prevMonth = month === 0 ? 11 : month - 1;
    let prevYear = prevMonth === 11 ? year - 1 : year;
    let nextMonth = month === 11 ? 0 : month + 1;

    let minDate = new Date();
    minDate.setMonth(prevMonth + 1);
    minDate.setFullYear(prevYear);

    let maxDate = new Date();
    maxDate.setMonth(nextMonth);
    maxDate.setFullYear(year);

    this.state = {
      minDate: minDate,
      maxDate: maxDate,
      onwarddate: '',
      fromSelect: '',
      toSelect: '',
      Stops: [],
      filteredFrom: [],
      filteredTo: []
    };
  }

  componentDidMount() {
    fetch('http://localhost:3001/BusStops')
      .then(res => res.json())
      .then(data => this.setState({ Stops: JSON.parse(data) }))
      .catch(err => console.log(err));
  }

  filterFromPlace = (event) => {
    setTimeout(() => {
      const results = this.state.Stops.filter(place => place.toLowerCase().startsWith(event.query.toLowerCase()));
      this.setState({ filteredFrom: results });
    }, 250);
  }

  filterToPlace = (event) => {
    setTimeout(() => {
      let results = this.state.Stops.filter(place => place.toLowerCase().startsWith(event.query.toLowerCase()));
      results = results.filter(r => r.toLowerCase() !== this.state.fromSelect.toLowerCase());
      this.setState({ filteredTo: results });
    }, 250);
  }

  handleRegister = (event) => {
    event.preventDefault();
    fetch('http://localhost:3001/CheckRoute', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        fromSelect: this.state.fromSelect,
        toSelect: this.state.toSelect
      })
    })
      .then(res => res.json())
      .then(data => {
        if (data.error === '') {
          this.props.setPlaces(this.state.fromSelect, this.state.toSelect);
          data.response.forEach(routeId => this.props.SetRouteId(routeId, 1));
          this.props.onRouteChange('search');
        } else {
          alert("No such route exists");
        }
      })
      .catch(err => console.log('Fetch error:', err));
  }

  render() {
    return (
      <div className='formwarp pa2 shadow-2 center'>
        <form>
          <div className='wraap pa2 center'>
            <div className="form-group col-md-10">
              <label htmlFor="onwarddate">Pick a Date: </label>
              <Calendar
                dateFormat="dd/mm/yy"
                value={this.state.onwarddate}
                minDate={this.state.minDate}
                maxDate={this.state.maxDate}
                onChange={(e) => this.setState({ onwarddate: e.value })}
                showIcon={true} />
            </div>

            <div className="form-group col-md-10">
              <label htmlFor="from">Source Place:</label>
              <AutoComplete
                value={this.state.fromSelect}
                suggestions={this.state.filteredFrom}
                completeMethod={this.filterFromPlace}
                size={30}
                placeholder="From Place"
                minLength={1}
                onChange={(e) => this.setState({ fromSelect: e.value })} />
              <App id="sourcePlaceVoice" />
            </div>

            <div className="form-group col-md-10">
              <label htmlFor="to">Destination</label>
              <AutoComplete
                value={this.state.toSelect}
                suggestions={this.state.filteredTo}
                completeMethod={this.filterToPlace}
                size={30}
                placeholder="To Place"
                minLength={1}
                onChange={(e) => this.setState({ toSelect: e.value })} />
              <App id="destinationPlaceVoice" />
            </div>

            <div className="form-group col-md-10">
              <input type='submit' value='Search Buses' onClick={this.handleRegister} className="btn btn-primary" />
            </div>
          </div>
        </form>
      </div>
    );
  }
}

export default Welcome;
