// App.js
import React from 'react';
import Artyom from 'artyom.js';
import ArtyomCommandsManager from './ArtyomCommands.js';

const Jarvis = new Artyom();

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      artyomActive: false,
      textValue: ''
    };

    // Initialize commands
    const CommandsManager = new ArtyomCommandsManager(Jarvis);
    CommandsManager.loadCommands();
  }

  startAssistant = () => {
    Jarvis.initialize({
      lang: "en-GB",
      debug: true,
      continuous: true,
      soundex: true,
      listen: true
    }).then(() => {
      Jarvis.say("Voice assistant activated.");
      this.setState({ artyomActive: true });
    }).catch((err) => {
      console.error("Error initializing Artyom:", err);
    });

    // Redirect recognized text to textarea
    Jarvis.redirectRecognizedTextOutput((recognizedText, isFinal) => {
      if (isFinal) {
        document.getElementById(this.props.id).value = recognizedText;
        this.setState({ textValue: recognizedText });
      }
    });
  };

  stopAssistant = () => {
    Jarvis.fatality().then(() => {
      this.setState({ artyomActive: false });
    }).catch((err) => {
      console.error("Error stopping Artyom:", err);
    });
  };

  render() {
    return (
      <div className="voice-resolver">
        <textarea id={this.props.id} value={this.state.textValue} style={{ width: "200px", height: "50px" }} />
        <input
          type="image"
          alt="Start Voice Assistant"
          disabled={this.state.artyomActive}
          src="https://robohash.org/1"
          style={{ width: "auto", height: "50px" }}
          onClick={() => this.startAssistant()}
        />
        <input
          type="image"
          alt="Stop Voice Assistant"
          disabled={!this.state.artyomActive}
          src="https://robohash.org/2"
          style={{ width: "auto", height: "50px" }}
          onClick={() => this.stopAssistant()}
        />
      </div>
    );
  }
}
