// C:\Users\golla\OneDrive\Desktop\bhan\Voice-based-Transport-Enquiry-System\voiceenquirysystem\src\Components\VoiceResolver\App.js
import React from 'react';
import Artyom from 'artyom.js';
import ArtyomCommandsManager from './ArtyomCommands.js';
import tachyons from 'tachyons';

const Jarvis = new Artyom();

export default class App extends React.Component {
    constructor (props, context){
        super(props, context);

        this.state = {
            artyomActive: false,
            textValue: ''
        };

        let CommandsManager = new ArtyomCommandsManager(Jarvis);
        CommandsManager.loadCommands();
    }

    startAssistant = () => {
        let _this = this;

        console.log("Artyom successfully started!");

        Jarvis.initialize({
            lang: "en-GB",
            debug: true,
            continuous: true,
            soundex: true,
            listen: true
        }).then(() => {
            Jarvis.say("Assistant activated.");
            _this.setState({
                artyomActive: true
            });
        }).catch((err) => {
            console.error("Error initializing Artyom:", err);
        });

        Jarvis.redirectRecognizedTextOutput((recognizedText, isFinal) => {
            if (isFinal) {
                document.getElementById(this.props.id).value = recognizedText;
                this.setState({ textValue: recognizedText });
            } else {
                document.getElementById(this.props.id).value = recognizedText;
            }
        });
    };

    stopAssistant = () => {
        Jarvis.fatality().then(() => {
            console.log("Artyom successfully stopped.");
            this.setState({ artyomActive: false });
        }).catch((err) => {
            console.error("Error stopping Artyom:", err);
        });
    };

    handleTextChange = (event) => {
        this.setState({
            textValue: event.target.value
        });
    };

    render() {
        return (
            <div className="bg-gold">
                <textarea onChange={this.handleTextChange} id={this.props.id} value={this.state.textValue} style={{width: "200px", height: "auto"}}/>
                <input type="image" alt="Start Artyom" disabled={this.state.artyomActive} src="https://robohash.org/1" style={{width: "auto", height: "50px"}} onClick={this.startAssistant}/>
                <input type="image" alt="Stop Artyom" disabled={!this.state.artyomActive} src="https://robohash.org/2" style={{width: "auto", height: "50px"}} onClick={this.stopAssistant}/>
            </div>
        );
    }
}
