// Message.js
import React, { Component } from 'react';
import './Message.css';

class Message extends Component {
  speak = (text) => {
    const synth = window.speechSynthesis;
    if (synth.speaking) {
      console.error('Already speaking...');
      return;
    }
    const speakText = new SpeechSynthesisUtterance(text);
    speakText.onend = () => console.log('Done speaking...');
    speakText.onerror = () => console.error('Error with speech synthesis');
    synth.speak(speakText);
  }

  handleClick = () => this.speak("I'm the leader bot. Click any bot for voice guidance.");

  render() {
    return (
      <div className='bot-helpers'>
        <img onClick={this.handleClick} alt='Leader Bot' src='https://robohash.org/leaderbot' style={{ width: '80px', height: 'auto' }} />
        <p className="bot-intro">Click on any bot to receive assistance.</p>

        <div className='bot-options'>
          <img alt='Book Bus Bot' onClick={() => this.speak("Click the button to Book a Bus")} src='https://robohash.org/2' style={{ width: '80px', height: 'auto' }} />
          <input type="button" value="Book a Bus" onClick={() => this.props.onRouteChange('bookBus')} readOnly /><br />

          <img alt='Location Bot' onClick={() => this.speak("Click the button to Search Bus Location")} src='https://robohash.org/locationbot' style={{ width: '80px', height: 'auto' }} />
          <input type="button" value="Search Bus Location" onClick={() => this.props.onRouteChange('locating')} readOnly /><br />

          <img alt='Sign In Bot' onClick={() => this.speak("Click the button to Sign In or Register")} src='https://robohash.org/signinbot' style={{ width: '80px', height: 'auto' }} />
          <input type="button" value="Sign In/Register" onClick={() => this.props.onRouteChange('signin')} readOnly /><br />

          <img alt='About Us Bot' onClick={() => this.speak("Click to learn more About Us")} src='https://robohash.org/aboutusbot' style={{ width: '80px', height: 'auto' }} />
          <input type="button" value="About Us" onClick={() => this.setState({ showPopup: true })} readOnly /><br />
        </div>
      </div>
    );
  }
}

export default Message;
