// Message.js
import React, { Component } from 'react';
import './Message.css';

class Message extends Component {
  constructor() {
    super();
    this.state = {
      showPopup: false
    };
  }

  togglePopup = () => {
    this.setState({ showPopup: !this.state.showPopup });
  }

  speak = (text) => {
    const synth = window.speechSynthesis;
    if (synth.speaking) {
      console.error('Already speaking...');
      return;
    }
    const speakText = new SpeechSynthesisUtterance(text);
    speakText.onend = () => console.log('Done speaking...');
    speakText.onerror = () => console.error('Speech synthesis error');
    synth.speak(speakText);
  }

  handleClick1 = () => this.speak("Click the button on my right side, to Book a bus");
  handleClick2 = () => this.speak("To Search Bus Location, Click the button on my right side");
  handleClick3 = () => this.speak("Looking to Sign in or Register?");
  handleClick4 = () => this.speak("Are you Interested in us? Click to Know more About us");
  handleClick = () => this.speak("I'm Leader Bot of this Westworld. Click on one of us to enable voice reply.");

  render() {
    return (
      <div className='broc'>
        <img onClick={this.handleClick} alt='Leader Bot' src='https://robohash.org/1?200x200' style={{ width: '80px', height: 'auto' }} />
        <p className="headeds" style={{ borderRadius: '55px 50px' }}>
          Hey! Click on one of us to enable voice reply. We'll help you.
        </p>

        <div className='wrapped xyz'>
          <input type="text" className="face" value="What are you looking for?" style={{ marginLeft: '10px', height: '50px' }} disabled /><br />

          <img alt='Book Bus Bot' onClick={this.handleClick1} src='https://robohash.org/2?200x200' style={{ width: '80px', height: 'auto' }} />
          <input type="text" value="Book a Bus" onClick={() => this.props.onRouteChange('bookBus')} readOnly /><br />

          <img alt='Location Bot' onClick={this.handleClick2} src='https://robohash.org/12?200x200' style={{ width: '80px', height: 'auto' }} />
          <input type="text" value="Search Bus Location" onClick={() => this.props.onRouteChange('locating')} readOnly /><br />

          <img alt='Sign In Bot' onClick={this.handleClick3} src='https://robohash.org/16?200x200' style={{ width: '80px', height: 'auto' }} />
          <input type="text" value="Sign In/Register" onClick={() => this.props.onRouteChange('signin')} readOnly /><br />

          <img alt='About Us Bot' onClick={this.handleClick4} src='https://robohash.org/13?200x200' style={{ width: '80px', height: 'auto' }} />
          <input type="text" value="About Us" onClick={this.togglePopup} readOnly /><br />

          {this.state.showPopup && (
            <Popup closePopup={this.togglePopup} />
          )}
        </div>
      </div>
    );
  }
}

class Popup extends React.Component {
  render() {
    return (
      <div className='popup'>
        <div className='popup_inner'>
          <h2>VOICE BASED AUTOMATED ENQUIRY SYSTEM V1.0</h2>
          <p>© All Rights Reserved</p>
          <ul>
            <li>Easy to use interface</li>
            <li>Voice-enabled updates and retrieval</li>
            <li>No human resource required</li>
            <li>24/7 availability</li>
          </ul>
          <button style={{ background: '#00ff00' }} onClick={this.props.closePopup}>Close</button>
        </div>
      </div>
    );
  }
}

export default Message;
