// ArtyomCommands.js
export default class ArtyomCommandsManager {
  constructor(ArtyomInstance) {
    this._artyom = ArtyomInstance;
  }

  loadCommands() {
    const Artyom = this._artyom;

    return Artyom.addCommands([
      {
        indexes: ["Hello", "Hi"],
        action: () => Artyom.say("Hello, how are you?")
      },
      {
        indexes: ["Book a bus"],
        action: () => Artyom.say("Click on Book a Bus to proceed.")
      },
      {
        indexes: ["Search bus location"],
        action: () => Artyom.say("Click on Search Bus Location to continue.")
      },
      {
        indexes: ["Sign in", "Register"],
        action: () => Artyom.say("Proceed to Sign In or Register.")
      },
      {
        indexes: ["About us"],
        action: () => Artyom.say("Learn more about us by clicking About Us.")
      }
    ]);
  }
}
