// App.js
import React from 'react';
import Artyom from 'artyom.js';
import ArtyomCommandsManager from './ArtyomCommands.js';

const Jarvis = new Artyom();

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      artyomActive: false,
      textValue: ''
    };

    const CommandsManager = new ArtyomCommandsManager(Jarvis);
    CommandsManager.loadCommands();
  }

  startAssistant = () => {
    let _this = this;
    Jarvis.initialize({
      lang: "en-GB",
      debug: true,
      continuous: true,
      soundex: true,
      listen: true
    }).then(() => {
      Jarvis.say("Assistant activated.");
      _this.setState({ artyomActive: true });
    }).catch((err) => {
      console.error("Error initializing Artyom:", err);
    });

    Jarvis.redirectRecognizedTextOutput((recognizedText, isFinal) => {
      if (isFinal) {
        document.getElementById(this.props.id).value = recognizedText;
        this.setState({ textValue: recognizedText });
      }
    });
  };

  stopAssistant = () => {
    Jarvis.fatality().then(() => {
      this.setState({ artyomActive: false });
    }).catch((err) => {
      console.error("Error stopping Artyom:", err);
    });
  };

  handleClick1 = (event) => {
    event.preventDefault();
    this.startAssistant();
  }

  handleClick2 = (event) => {
    event.preventDefault();
    this.stopAssistant();
  }

  render() {
    return (
      <div className="bg-gold">
        <textarea onChange={(e) => this.setState({ textValue: e.target.value })} id={this.props.id} value={this.state.textValue} style={{ width: "200px", height: "auto" }} />
        <input type="image" alt="Start Artyom" disabled={this.state.artyomActive} src="https://robohash.org/1" style={{ width: "auto", height: "50px" }} onClick={this.handleClick1} />
        <input type="image" alt="Stop Artyom" disabled={!this.state.artyomActive} src="https://robohash.org/2" style={{ width: "auto", height: "50px" }} onClick={this.handleClick2} />
      </div>
    );
  }
}
