import React, { Component } from 'react';
import Artyom from 'artyom.js';
import ArtyomCommandsManager from './ArtyomCommands';

const Jarvis = new Artyom();

export default class VoiceResolver extends Component {
    constructor(props) {
        super(props);
        this.state = {
            artyomActive: false
        };
        this.CommandsManager = new ArtyomCommandsManager(Jarvis);
    }

    componentDidMount() {
        this.startAssistant();
    }

    startAssistant = () => {
        this.CommandsManager.loadCommands();
        Jarvis.initialize({
            lang: "en-GB",
            debug: true,
            continuous: true,
            listen: true
        }).then(() => {
            console.log("Artyom initialized and listening.");
            this.setState({ artyomActive: true });
            Jarvis.say("Voice assistant is now active.");
        }).catch(err => {
            console.error("Failed to start Artyom:", err);
        });
    };

    stopAssistant = () => {
        Jarvis.fatality().then(() => {
            console.log("Artyom stopped.");
            this.setState({ artyomActive: false });
        });
    };

    render() {
        return (
            <div>
                <button onClick={this.startAssistant} disabled={this.state.artyomActive}>Start Voice Assistant</button>
                <button onClick={this.stopAssistant} disabled={!this.state.artyomActive}>Stop Voice Assistant</button>
            </div>
        );
    }
}
